  `resource_ids` varchar(200) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `client_secret` varchar(200) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `scope` varchar(200) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `authorized_grant_types` varchar(200) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `web_server_redirect_uri` varchar(200) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `authorities` varchar(200) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `access_token_validity` int DEFAULT NULL,
  `refresh_token_validity` int DEFAULT NULL,
  `additional_information` varchar(4096) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `autoapprove` varchar(200) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  PRIMARY KEY (`client_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

/*Data for the table `oauth_client_details` */


/*Table structure for table `oauth_client_token` */

